}

class APIClient {
  constructor(baseUrl) {
    this.baseUrl = baseUrl;
  }

  async fetch(endpoint) {
    const response = await fetch(this.baseUrl + endpoint);
    return response.json();
  }

  async post(endpoint, data) {
    const response = await fetch(this.baseUrl + endpoint, {
      method: 'POST',
      body: JSON.stringify(data)
    });
    return response.json();
  }
}
